with data_frame as (
    select t.*
         ,t1.target_table
         ,t2.partition_column
         ,t3.partition_column_2
         ,t4.target_partition
    from t_script_info t
             left join lateral ( select regexp_matches(upper(t.script_content), 'INSERT OVERWRITE TABLE [A-Za-z0-9_\.]+')                            as target_table       ) t1 on True
             left join lateral ( select regexp_matches(upper(t.script_content), 'PARTITIONED BY\([A-Za-z0-9_]+')                                     as partition_column   ) t2 on True
             left join lateral ( select regexp_matches(upper(t.script_content), 'INSERT OVERWRITE TABLE [A-Za-z0-9_\.]+\sPARTITION\(\w+')            as partition_column_2 ) t3 on True
             left join lateral ( select regexp_matches(upper(regexp_replace(t.script_content, E'[\\n\\r]', ' ', 'g')),',.*?FROM\s*\w*\.') as target_partition   ) t4 on True
    where t.app_name = 'cmsk_basic'
      and t.script_type = 'HIVE'
--    and lower(t.script_content) not like '%p_day%'
) , in_use as (
    select distinct
        coalesce(log_app_name,app_name)   as app_name
                  ,coalesce(node_name,script_name)   as script_name
                  ,case when t.log_app_name is null then 'no_use'
                        when t.app_name     is null then 'not_found'
                        else 'OK' end as found_type
    from t_script_in_use t
    where coalesce(log_app_name,app_name) = 'cmsk_basic'
) ,result as (
    select t.app_name
         ,t.script_name
         ,split_part(t.target_table[1],'_','-1')                                      as table_type
         ,replace(t.target_table[1],'INSERT OVERWRITE TABLE ','')                     as target_table
         ,coalesce(split_part(t.partition_column[1],'(',2)
        ,split_part(t.partition_column_2[1],'(',2))                         as partition_column
         ,replace(regexp_replace(
                          split_part(
                                  split_part(t.target_partition[1],' FROM',1)
                              ,',',-1),'^,*\s*|\t*\s*$',''),' ','') as target_partition
         ,t1.found_type
         ,t.script_content
    from data_frame t
             left join in_use t1
                       on upper(t.app_name) = upper(t1.app_name)
                           and upper(t1.script_name) =upper(replace(t.script_name,'.sql',''))

)



