create table t_all_table(
                            app_name   text
    ,table_name text
) ;

select table_name
     ,count(*)
     ,string_agg(app_name,',')
from t_all_table
group by table_name
order by 2 desc ;

select table_name
     ,count(*)
     ,string_agg(app_name,',')
from t_all_table_size
group by table_name
order by 2 desc ;

with table_size as (select t.app_name
                         , t.table_name
                         , coalesce(
            round(
                        cast(t1.replica_size as decimal(38, 8)) / 1024 / 1024
                , 5)
        , 0) as "replica_size(MB)"
                    from t_all_table t
                             left join t_all_table_size t1 on t.app_name = t1.app_name and t.table_name = t1.table_name
                    order by replica_size)
select t.*,t1.table_name
from table_size t
         left join(select distinct lower(table_name) as table_name from t_table_inuse) t1
                  on t.table_name = t1.table_name
order by 3 desc,app_name,t.table_name
;

with frame as (select t.script_id
                    , unnest(t.source_tables) st
                    , unnest(t.target_tables) tt
               from t_dialog_info t)
   , combine as (select t1.script_id
                      , lower(t1.st) as table_name
                 from frame t1
                 union all
                 select t1.script_id
                      , lower(t1.tt) as table_name
                 from frame t1)
select distinct t2.script_id,t2.table_name
from combine t2
where t2.table_name is not null
  and t2.table_name = 'ha02_first_order_issue_stg_ss' ;

with frame as (
    select t.app_name
         ,t.script_content
         ,jsonb_path_query(cast(t.script_content as jsonb), '$.job.content[*].reader.parameter.connection[*]') as source_table
         ,jsonb_path_query(cast(t.script_content as jsonb), '$.job.content[*].writer.parameter.connection[*]') as target_table
    from t_script_info t
    where t.script_type = 'DI'
)
select t.app_name
     ,regexp_replace(t.source_table->>'table', '[\[\]\"\\`]', '', 'g') as st
     ,regexp_replace(t.target_table->>'table', '[\[\]\"\\]', '', 'g') as tt
     ,regexp_match(t.target_table->>'jdbcUrl','[\d\.]+:\d+/\w+')
from frame t
where t.target_table is not null
  and(app_name = 'cmsk_hrbi'
    or target_table->>'jdbcUrl' like '%{100.78.5.169%'
    or target_table->>'jdbcUrl' like '%{100.100.4.69%') ;




