drop view public.t_dialog_table_list cascade;
create view t_dialog_table_list(script_id, app_name, script_type, script_name, job_name, st, tt) as
SELECT script_id,
       app_name,
       script_name,
       script_type,
       path_arr[array_length(path_arr, 1) - 2]                        AS job_name,
       array_remove(array_agg(DISTINCT st1), NULL::character varying) AS st,
       array_remove(array_agg(DISTINCT tt1), NULL::character varying) AS tt
FROM (SELECT t1.id                                                  AS script_id,
             t1.app_name,
             t1.script_name,
             t1.script_type,
             s_t.st1,
             t_t.tt1,
             regexp_split_to_array(t1.script_path::text, '/'::text) AS path_arr
      FROM t_script_info t1
               LEFT JOIN t_dialog_info t_1 ON t_1.script_id = t1.id
               LEFT JOIN LATERAL unnest(t_1.source_tables) s_t(st1) ON true
          LEFT JOIN LATERAL unnest(t_1.target_tables) t_t(tt1) ON true) t
GROUP BY script_id, app_name, script_name, path_arr, script_type;

alter table t_dialog_table_list
    owner to fastapi;

