drop view if exists parser.controlm_details cascade;
create or replace view parser.controlm_details as
select max(case when t.name = '%%UCM-ACCOUNT'  then lower(t1.attribute_value) else null end) as app_name
     ,max(case when t.name = '%%UCM-JOB_NAME' then lower(t1.attribute_value) else null end) as job_name
     ,max(case when t.name = '%%UCM-APP_NAME' then lower(t1.attribute_value) else null end) as job_type
from parser.controlm_objects t
         left join parser.controlm_attributes t1
                   on t.id = t1.object_id
                       and t1.attribute_name = 'VALUE'
where t.name in ('%%UCM-JOB_NAME','%%UCM-ACCOUNT','%%UCM-APP_NAME')
group by t.parent_id;


drop view if exists public.t_script_ctm_cd_job_compare cascade;
create or replace view public.t_script_ctm_cd_job_compare as
with tmp_ctm as (
    select distinct
           app_name
          ,job_name
      from parser.controlm_details
), tmp_script as (
    select app_name
          ,job_name
          ,string_agg(distinct script_name,',') as script_name
      from public.t_dialog_table_list
     group by app_name
            ,job_name
), tmp_cd_job as (
    select distinct
           app_name
          ,job_name
      from public.t_job_list
)
select coalesce(t.app_name,t1.app_name,tj.app_name) as app_name
     ,coalesce(t.job_name,t1.job_name,tj.job_name) as job_name
     ,t.script_name
     ,case when t.app_name  is null or t.job_name  is null then '0' else 1 end as script
     ,case when t1.app_name is null or t1.job_name is null then '0' else 1 end as ctm
     ,case when tj.app_name is null or tj.job_name is null then '0' else 1 end as cd_job
from tmp_script t
         full outer join tmp_ctm t1
                         on  t.app_name = t1.app_name
                             and t.job_name = t1.job_name
         full outer join tmp_cd_job tj
                         on  tj.job_name = coalesce(t.job_name,t1.job_name)
                             and tj.app_name = coalesce(t.app_name,t1.app_name)
;


